$('form').submit(function (e) {
    console.log('button pressed');
    var form = $(this);
    
    var searchTerms = form.find('input.search').val();
    
    $.ajax({
      'url': 'http://apis.is/company',
      'type': 'GET',
      'dataType': 'json',
